ALTER PROCEDURE Balance_Update(@Amount_Update DECIMAL (20,2),@AccountNumber int,@UpdateType varchar(20))ASIF @UpdateType='credit'BEGINUPDATE account    SET accountbalance =  accountbalance+ @Amount_Update    WHERE AccountNumber = @AccountNumber;ENDIF @UpdateType='debit'BEGINUPDATE account    SET accountbalance =  accountbalance - @Amount_Update    WHERE AccountNumber = @AccountNumber;END    EXEC Balance_Update @Amount_Update= 5000,@AccountNumber= 15659384, @UpdateType= 'debit'
